package com;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
// son una estructura de datos que almacena datos en comun  y una ves declarado su tamaño y longitud
		// este no puede crecer o cambiar
		
		char[] nombre = new char[4]; //
		
		// asignando datos o valores a cada posicion del array 
		nombre [0]='A';
		nombre [1]='1';
		nombre [2]='e';
		nombre [3]='x';
		
		System.out.println(nombre[3]);
		
		
	}
	public static void imprime(String mt_text){
		System.out.println(mt_text);
	}
}
